mapper.select:
        select *
        	from material
			     		where type=#{material_type} 
					        	  and operation_id=#{operation_id} 
					        	  	order by create_time desc
					        	  		limit #{startindex},#{pagesize};

     	select count(*) from material
			     		where type=#{material_type} 
					        	  and operation_id=#{operation_id}

mapper.update:
update material 
	set flag_output=1
	where id=#{id}

update material 
	set flag_output=0
	where id=#{id}

mapper.select:
select id 
	from material
	where flag_output=1 
	      and operation_id=#{operation_id}
	      and type=#{type}
